import { PlusOutlined } from "@ant-design/icons";
import React, { useCallback, useState } from "react";

const PostImages = ({ images }) => {
  console.log(images);
  //이미지 확대 및 캐루셀
  const [showImagesZoom, setShowImgagesZoom] = useState(false);
  const onZoom = useCallback(() => {
    setShowImgagesZoom(true);
  }, []);

  if (images.length === 1) {
    return (
      <>
        <img
          width="100%"
          role="presentation"
          src={images[0].src}
          alt={images[0].src}
          onClick={onZoom}
        />
      </>
    );
  }
  if (images.length === 2) {
    return (
      <>
        <img
          style={{ width: "50%" }}
          role="presentation"
          src={images[0].src}
          alt={images[0].src}
          onClick={onZoom}
        />
        <img
          width="50%"
          role="presentation"
          src={images[1].src}
          alt={images[1].src}
          onClick={onZoom}
        />
      </>
    );
  }
  return (
    <div style={{ display: "flex" }}>
      <img
        width="50%"
        role="presentation"
        src={images[0].src}
        alt={images[0].src}
        onClick={onZoom}
      />
      <div
        role="presentation"
        onClick={onZoom}
        style={{
          border: "1px solid red",
          width: "50%",
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          flexDirection: "column",
        }}
      >
        <PlusOutlined />
        <div>{images.length - 1} 개의 사진 더 보기</div>
      </div>
    </div>
  );
};

export default PostImages;
