import React, { useState } from "react";
import Slick from "react-slick";
import styled from "styled-components";

const ImagesZoom = ({ images, onClose }) => {
  const [currentSlide, setCurrentSlide] = useState(0);
  return (
    <Overlay>
      <header className="header">
        <h1>상세 이미지</h1>
        <button onClick={onClose} className="close-btn">
          X
        </button>
      </header>
      <div>
        <div>
          <Slick
            initialSlide={0}
            afterChange={(slide) => setCurrentSlide(slide)}
            infinite
            arrows={false}
            slidesToShow={1}
          >
            {images.map((img) => (
              <div key={img}>
                <img
                  src={img.src}
                  alt={img.src}
                  style={{ width: "100px", height: "100px" }}
                />
              </div>
            ))}
          </Slick>
        </div>
      </div>
    </Overlay>
  );
};

// function`` 함수 호출 방법

const Overlay = styled.div`
  /* fixed를 전체 화면으로 채우는 방법 */
  position: fixed;
  z-index: 5000;
  left: 0;
  right: 0;
  top: 0;
  bottom: 0;
  background-color: #fff;

  .header {
    height: 44px;
    position: relative;
    padding: 0;
    text-align: cetner;

    & h1 {
      margin: 0;
      font-size: 17px;
      color: #333;
      line-height: 44px;
    }

    & button {
      position: absolute;
      rigth: 0;
      bottom: 0;
    }
  }
`;

export default ImagesZoom;
