//initial state
const initialState = {
  name: "",
  age: null,
  password: null,
};

//action creator
const changeNickname = () => {
  return {
    type: "CHANGE_NAME",
    data: {
      name,
    },
  };
};

// (이전상태, 액션) => 다음상태 를 만들어내는 함수
const rootReducer = (state = initialState, action) => {
  switch (action.type) {
    case "CHANGE_NAME":
      return {
        ...state,
      };

    default:
      break;
  }
};

export default rootReducer;
