import React from "react";
import { Card, Popover } from "antd";
import {
  HeartOutlined,
  MessageOutlined,
  RetweetOutlined,
} from "@ant-design/icons";

const PostCard = ({ post }) => {
  return (
    <div>
      <Card
        cover={post.Images[0] && <PostImages images={post.images} />}
        actions={[
          <RetweetOutlined />,
          <HeartOutlined />,
          <MessageOutlined />,
          <Popover></Popover>,
        ]}
      >
        <Image />
        <Content />
      </Card>
    </div>
  );
};

export default PostCard;
