import React, { useCallback, useState } from "react";

const PostImages = ({ images }) => {
  //이미지 확대 및 캐루셀
  const [showImagesZoom, setShowImgagesZoom] = useState(false);
  const onZoom = useCallback(() => {
    setShowImgagesZoom(true);
  }, []);

  if (images.length === 1) {
    return (
      <>
        <img
          role="presentation"
          src={images[0].src}
          alt={images[0].src}
          onClick={onZoom}
        />
      </>
    );
  }
  if (images.length === 2) {
    return (
      <>
        <img
          role="presentation"
          src={images[0].src}
          alt={images[0].src}
          onClick={onZoom}
        />
      </>
    );
  }
  return <div>구현중입니다. </div>;
};

export default PostImages;
