const initialState = {
  user: {
    isLoggedInf: false,
    user: null,
    signUpData: {},
    loginData: {},
  },
  post: {
    mainPosts: [],
  },
};

//action creator

const login = (data) => {
  return {
    type: "LOG_IN",
    data,
  };
};

// (이전상태, 액션) => 다음상태 를 만들어내는 함수
const rootReducer = (state = initialState, action) => {
  console.log(action.type);
  switch (action.type) {
    case "LOG_IN":
      return {
        uset: {},
      };

    default:
      break;
  }
};

export default rootReducer;
