//initial state
const initialState = {
  name: "",
  age: null,
  password: null,
};

//action creator
const changeNickname = (data) => {
  return {
    type: "CHANGE_NAME",
    data,
  };
};

// (이전상태, 액션) => 다음상태 를 만들어내는 함수
const rootReducer = (state = initialState, action) => {
  console.log(action);
  switch (action.type) {
    case "CHANGE_NAME":
      return {
        ...state,
        name: action.data,
      };

    default:
      break;
  }
};

export default rootReducer;
